import { useState } from "react";
import { Link } from "react-router-dom";
import NavButton from "./NavButton";
import { FiMenu, FiX } from "react-icons/fi"; // Import menu icons

const NavMenu = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [hoverColor, setHoverColor] = useState("#201C35"); // Default navbar background color

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const handleMouseEnter = (color) => {
    console.log(`Hover color set to: ${color}`); // Add log to check the color
    setHoverColor(color); // Set navbar background color on hover
  };
  
  const handleMouseLeave = () => {
    console.log("Hover color reset to default");
    setHoverColor("#201C35"); // Reset navbar background color when not hovering
  };
  

  return (
    <nav className="navmenu" style={{ backgroundColor: "#FFE347" }}>
      <div className="nav-container">
        {/* Logo */}
        <div className="nav-logo">
          <Link to="/">
            <img src="/images/M-logo.png" alt="Logo" className="logo-img" />
          </Link>
        </div>

        {/* Hamburger Button */}
        <button className="hamburger" onClick={toggleMenu}>
          {isOpen ? <FiX /> : <FiMenu />}
        </button>

        {/* Menu Links */}
        <ul className={`nav-links ${isOpen ? "active" : ""}`}>
          <NavButton
            to="/coding"
            className="nav-coding"
            onMouseEnter={() => handleMouseEnter("#FFE347")} // Change navbar background color to yellow on hover
            onMouseLeave={handleMouseLeave} // Reset navbar background color when not hovering
          >
            CODING
          </NavButton>
          <NavButton
            to="/comics"
            className="nav-comics"
            onMouseEnter={() => handleMouseEnter("#23F0C7")} // Change navbar background color to teal on hover
            onMouseLeave={handleMouseLeave} // Reset navbar background color when not hovering
          >
            COMICS
          </NavButton>
          <NavButton
            to="/contact"
            className="nav-contact"
            onMouseEnter={() => handleMouseEnter("#EF767A")} // Change navbar background color to pink on hover
            onMouseLeave={handleMouseLeave} // Reset navbar background color when not hovering
          >
            CONTACT
          </NavButton>
        </ul>
      </div>
    </nav>
  );
};

export default NavMenu;
