import React, { useEffect, useState } from 'react';

export default function CodingPage() {
  const [repos, setRepos] = useState([]);
  const githubUsername = "mstyles1"; // Replace with your GitHub username

  useEffect(() => {
    fetch(`https://api.github.com/users/${githubUsername}/repos`)
      .then(response => response.json())
      .then(data => setRepos(data))
      .catch(error => console.error('Error fetching repositories:', error));
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">My GitHub Portfolio</h1>
      <p>Check out my projects on GitHub:</p>
      <ul className="mt-4 space-y-2">
        {repos.map(repo => (
          <li key={repo.id} className="border p-2 rounded-md">
            <a href={repo.html_url} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:underline">
              {repo.name}
            </a>
            <p className="text-sm text-gray-600">{repo.description || 'No description available'}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}
